import  React, {Component} from 'react';
import axios from 'axios';

import './css/style.css'

export default  class addEmployee extends  Component{


    constructor(props){
        super(props);

        this.onChangetFirstName = this.onChangetFirstName.bind(this);
        this.onChangetLastName = this.onChangetLastName.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangetDoB = this.onChangetDoB.bind(this);
        this.onChangetLastName = this.onChangetLastName.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangetDe = this.onChangetDoB.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            foodname: '',
            price: '',
            size:'',
            station:''
        }

        this.state.station = this.props.match.params.id;
    }
    onChangeFoodName(e){
        this.setState( {
           foodname: e.target.value
        });
    }
    onChangePrice(e){
        this.setState( {
            price: e.target.value
        });
    }
    onChangeSize(e){
        this.setState( {
            size: e.target.value
        });
    }
    onChangeStation(e){
        this.setState( {
            station: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        const obj = {
            foodname : this.state.foodname,
            price : this.state.price,
            size : this.state.size,
            station : this.state.station
        };

        console.log('Update id '+this.props.match.params.id);

        axios.post('http://localhost:4000/trainFoodAdmin/adminaddfood',obj)
        .then(res => {
            alert("Food Add Successfully");
            this.setState({
                foodname :'',
                price :'',
                size :'',
                station:''
    
            })
            console.log(res.data)});
        // this.props.history.push('/signIn');
        window.location.replace('/adminviewfood/'+this.state.station);
    }

    render() {
        return(
            <div className='container'>
                <div className='row'>
                    <div className='col-lg-4'>
                        <h2 className= 'tittle'>Employee Management</h2>
                        <hr/>
                        <h3 className="text-center">Add New Employee</h3>
                        <hr/>
                        <img src = "https://img.freepik.com/free-vector/done-concept-illustration_114360-3060.jpg?w=2000" width={300} />
                        <hr/>
                    </div>
                    <div className='col-lg-8'>
                        <form onSubmit={this.onSubmit}>
                            <div class="form-row">
                                <div class="form-group col-md-6">
                                    <label>First Name</label>
                                    <input type="text" class="form-control"  value={this.state.fname} onChange = {this.onChangetFirstName}/>
                                </div>
                                <div class="form-group col-md-6">
                                    <label>Last Name</label>
                                    <input type="text" class="form-control" value={this.state.lname} onChange = {this.onChangetLastName}/>
                                </div>
                            </div>
                            <div class="form-group">
                                <label>email Address</label>
                                <input type="email" class="form-control" value={this.state.email} onChange = {this.onChangeEmail}/>
                            </div>
                            <div class="form-row">
                                <div class="form-group col-md-6">
                                    <label>Date of Birth</label>
                                    <input type="date" class="form-control"  value={this.state.dob} onChange = {this.onChangetDoB}/>
                                </div>
                                <div class="form-group col-md-6">
                                    <label>Age</label>
                                    <input type="number" readOnly class="form-control" value={this.state.age} onChange = {this.onChangetAge}/>
                                </div>
                            </div>
                            <div class="form-group">
                                <label>Salary (LKR)</label>
                                <input type="number" class="form-control" value={this.state.salary} onChange = {this.onChangeSalary}/>
                            </div>
                            <div class="form-group">
                                <label>Department</label>
                                {/* <input type="number" class="form-control" value={this.state.salary} onChange = {this.onChangeSalary}/> */}
                                <select class="form-control" value={this.state.department} onChange = {this.onChangeDepartment}>
                                    <option>select department....</option>
                                    <option value = "IT">IT</option>
                                </select>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}
