import  React, {Component} from 'react';
import axios from 'axios';

import employeeTableRow from './employeeTableRow';
import './css/style.css'

export default  class viewEmployee extends  Component{


    constructor(props){
        super(props);

        this.state = {employees : []};

    }

    componentDidMount() {
        axios.get('http://localhost:7149/api/Employee')
            .then(response => {
                this.setState({employees : response.data});

            })
            .catch(function (error){
                console.log(error);
            })
    }

    tabRow(){
        return this.state.employees.map(function (object, i){
            return <employeeTableRow obj = {object} key = {i}/>;
        });
    }


    render() {
        return(
            <div className='container'>
                <div className='row'>
                    <h2>Current Employees Details</h2>
                    <table className="table table-striped" style = {{marginTop :20}}>
                            <thead>
                                <tr>
                                    {/* <th>id</th> */}
                                    <th>First Name</th>
                                    <th>Last Name</th>
                                    <th>email</th>
                                    <th>DoB</th>
                                    <th>Age</th>
                                    <th>salary</th>
                                    <th>DoB</th>
                                    <th colSpan="3">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.tabRow()}
                            </tbody>
                    </table>
                </div>
            </div>
        )
    }
}
